import 'dart:convert';
import 'dart:typed_data';

import 'package:flutter/material.dart';
import 'package:image_picker/image_picker.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Demo',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: const MyHomePage(title: 'Flutter Demo Home Page'),
    );
  }
}

class MyHomePage extends StatefulWidget {
  const MyHomePage({Key? key, required this.title}) : super(key: key);

  final String title;

  @override
  State<MyHomePage> createState() => _MyHomePageState();
}

const String img =
    "data:image/jpeg;base64,";
const String imgCut =
    "";
const String imgDariKamu =
    "";

class _MyHomePageState extends State<MyHomePage> {
  int _counter = 0;

  String? _base64Img;

  final TextEditingController _tfController = TextEditingController();

  void _incrementCounter() {
    setState(() {
      _counter++;
    });
  }

  @override
  void initState() {
    super.initState();
  }

  final _imgPicker = ImagePicker();

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(widget.title),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            const Text(
              'You have pushed the button this many times:',
            ),
            Text(
              '$_counter',
              style: Theme.of(context).textTheme.headline4,
            ),
            ElevatedButton(
              onPressed: () async {
                XFile? _upImg =
                    await _imgPicker.pickImage(source: ImageSource.gallery);
                if (_upImg != null) {
                  Uint8List _pickedBytes = await _upImg.readAsBytes();
                  setState(() {
                    _base64Img = base64.encode(_pickedBytes);
                  });
                  _tfController.clear();
                  _tfController.text = _base64Img ?? '';
                }
              },
              child: const Text("Masukkan Img"),
            ),
            Container(
              height: 200,
              width: 200,
              color: Colors.black,
              child: () {
                if (_base64Img != null) {
                  return Image.memory(
                    base64.decode(_base64Img!),
                    errorBuilder: (context, obj, stack) {
                      return const Placeholder();
                    },
                  );
                } else {
                  return const SizedBox();
                }
              }(),
            ),
            SizedBox(
              width: 400,
              child: TextFormField(
                controller: _tfController,
                maxLines: 3,
              ),
            )
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: _incrementCounter,
        tooltip: 'Increment',
        child: const Icon(Icons.add),
      ),
    );
  }
}
